import React, { useEffect, useState } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import { Loader, ArrowLeft, Plus } from 'lucide-react';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import SlideService from '../services/SlideService';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

const SLIDE_TEMPLATES = [
  { type: 'title', label: 'Title Template', content: { content: '' } },
  { type: 'basic', label: 'Basic Template', content: { content: '' } },
  { type: 'mcq', label: 'MC Template', content: { question: '', options: ['', '', '', ''], correct: [] } },
  { type: 'open', label: 'Open-ended', content: { question: '', answer: '' } },
];

const quillModules = {
  toolbar: [
    [{ 'font': [] }, { 'size': [] }],
    ['bold', 'italic', 'underline', 'strike'],
    [{ 'color': [] }, { 'background': [] }],
    [{ 'script': 'sub'}, { 'script': 'super' }],
    [{ 'header': 1 }, { 'header': 2 }, 'blockquote', 'code-block'],
    [{ 'list': 'ordered'}, { 'list': 'bullet' }, { 'indent': '-1'}, { 'indent': '+1' }],
    [{ 'direction': 'rtl' }, { 'align': [] }],
    ['link', 'image', 'video'],
    ['clean']
  ]
};

const quillFormats = [
  'font', 'size', 'bold', 'italic', 'underline', 'strike', 'color', 'background',
  'script', 'header', 'blockquote', 'code-block', 'list', 'bullet', 'indent',
  'direction', 'align', 'link', 'image', 'video'
];

const SLIDE_HEIGHT = 320;

const PresentationEditor = () => {
  const { courseId, presentationId } = useParams();
  const navigate = useNavigate();
  const [slides, setSlides] = useState([]);
  const [slideIds, setSlideIds] = useState([]);
  const [selectedSlide, setSelectedSlide] = useState(0);
  const [loading, setLoading] = useState(true);
  const [template, setTemplate] = useState('title');

  useEffect(() => {
    const fetchSlides = async () => {
      setLoading(true);
      try {
        const data = await SlideService.getSlides(courseId, presentationId);
        setSlides(data.map(s => ({ ...s, id: undefined })));
        setSlideIds(data.map(s => s.id));
        setSelectedSlide(0);
        console.log('[PresentationEditor] Slides loaded:', data);
      } catch (err) {
        console.error('[PresentationEditor] Error loading slides:', err);
        setSlides([{ type: 'title', content: '' }]);
        setSlideIds([null]);
      } finally {
        setLoading(false);
      }
    };
    fetchSlides();
  }, [courseId, presentationId]);

  const saveSlide = async (slide, idx) => {
    try {
      if (slideIds[idx]) {
        await SlideService.updateSlide(courseId, presentationId, slideIds[idx], slide);
        console.log('[PresentationEditor] Slide updated:', slide);
      } else {
        const docRef = await SlideService.addSlide(courseId, presentationId, slide);
        setSlideIds(prev => prev.map((id, i) => i === idx ? docRef.id : id));
        console.log('[PresentationEditor] Slide added:', slide);
      }
    } catch (err) {
      console.error('[PresentationEditor] Error saving slide:', err);
    }
  };

  const handleDeleteSlide = async (idx) => {
    if (slideIds[idx]) {
      try {
        await SlideService.deleteSlide(courseId, presentationId, slideIds[idx]);
        console.log('[PresentationEditor] Slide deleted:', slideIds[idx]);
      } catch (err) {
        console.error('[PresentationEditor] Error deleting slide:', err);
      }
    }
    setSlides(prev => prev.filter((_, i) => i !== idx));
    setSlideIds(prev => prev.filter((_, i) => i !== idx));
    setSelectedSlide(prev => Math.max(0, prev === idx ? prev - 1 : prev > idx ? prev - 1 : prev));
  };

  // Drag-and-drop reorder
  const onDragEnd = (result) => {
    if (!result.destination) return;
    const from = result.source.index;
    const to = result.destination.index;
    if (from === to) return;
    const newSlides = Array.from(slides);
    const newSlideIds = Array.from(slideIds);
    const [removedSlide] = newSlides.splice(from, 1);
    const [removedId] = newSlideIds.splice(from, 1);
    newSlides.splice(to, 0, removedSlide);
    newSlideIds.splice(to, 0, removedId);
    setSlides(newSlides);
    setSlideIds(newSlideIds);
    setSelectedSlide(to);
    // Optionally: update order in Firestore (add an 'order' field)
  };

  // Add slide below selected
  const handleAddSlide = async (type) => {
    const templateObj = SLIDE_TEMPLATES.find(t => t.type === type);
    const newSlide = { ...templateObj.content, type };
    const insertIdx = selectedSlide + 1;
    setSlides(prev => [
      ...prev.slice(0, insertIdx),
      newSlide,
      ...prev.slice(insertIdx)
    ]);
    setSlideIds(prev => [
      ...prev.slice(0, insertIdx),
      null,
      ...prev.slice(insertIdx)
    ]);
    setSelectedSlide(insertIdx);
    setTimeout(() => saveSlide(newSlide, insertIdx), 0);
  };

  // Save slide on edit
  const handleSlideChange = (field, value) => {
    setSlides(prev => prev.map((slide, idx) => idx === selectedSlide ? { ...slide, [field]: value } : slide));
    setTimeout(() => saveSlide({ ...slides[selectedSlide], [field]: value }, selectedSlide), 0);
  };

  // MCQ: toggle bold for correct answer
  const toggleMCQCorrect = (optionIdx) => {
    setSlides(prev => prev.map((slide, idx) => {
      if (idx !== selectedSlide) return slide;
      const correct = slide.correct || [];
      const isCorrect = correct.includes(optionIdx);
      return {
        ...slide,
        correct: isCorrect ? correct.filter(i => i !== optionIdx) : [...correct, optionIdx]
      };
    }));
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-primary-50 to-secondary-50 py-8">
      <div className="max-w-6xl mx-auto px-4 flex gap-8">
        {/* Slide Preview Panel */}
        <div className="w-1/4" style={{ maxHeight: SLIDE_HEIGHT * 2 + 64, overflowY: 'auto' }}>
          <div className="flex items-center mb-4">
            <button
              className="btn-primary flex items-center"
              onClick={() => handleAddSlide(template)}
            >
              <Plus className="h-4 w-4 mr-1" /> Slide
            </button>
            <select
              className="ml-2 input-field"
              value={template}
              onChange={e => setTemplate(e.target.value)}
            >
              {SLIDE_TEMPLATES.map(t => (
                <option key={t.type} value={t.type}>{t.label}</option>
              ))}
            </select>
          </div>
          <DragDropContext onDragEnd={onDragEnd}>
            <Droppable droppableId="slides-droppable">
              {(provided) => (
                <div ref={provided.innerRef} {...provided.droppableProps}>
                  {slides.map((slide, idx) => (
                    <Draggable key={idx} draggableId={`slide-${idx}`} index={idx}>
                      {(provided, snapshot) => (
                        <div
                          ref={provided.innerRef}
                          {...provided.draggableProps}
                          {...provided.dragHandleProps}
                          className={`border rounded p-2 cursor-pointer flex items-center justify-between mb-2 ${selectedSlide === idx ? 'border-primary-600 bg-primary-50' : 'border-gray-200 bg-white'}`}
                          style={{ minHeight: 60, ...provided.draggableProps.style }}
                          onClick={() => setSelectedSlide(idx)}
                        >
                          <div className="flex-1" style={{ maxHeight: SLIDE_HEIGHT, overflow: 'hidden' }}>
                            <div className="text-xs font-semibold text-gray-700">{slide.type.charAt(0).toUpperCase() + slide.type.slice(1)} Slide</div>
                            {/* Render preview for title/basic as HTML, others as text */}
                            {(slide.type === 'title' || slide.type === 'basic') && (
                              <div dangerouslySetInnerHTML={{ __html: slide.content || '<span class=\'text-gray-400\'>Untitled</span>' }} className="text-xs" />
                            )}
                            {slide.type === 'mcq' && (
                              <div>
                                <div className="font-bold text-xs">{slide.question || 'Untitled'}</div>
                                <ol className="text-xs text-gray-500 list-decimal ml-4">
                                  {slide.options.map((opt, i) => (
                                    <li key={i} className={slide.correct?.includes(i) ? 'font-bold text-primary-700' : ''}>{opt}</li>
                                  ))}
                                </ol>
                              </div>
                            )}
                            {slide.type === 'open' && (
                              <div>
                                <div className="font-bold text-xs">{slide.question || 'Untitled'}</div>
                              </div>
                            )}
                          </div>
                          <div className="flex flex-col items-center ml-2 gap-1">
                            <button className="btn-secondary px-1 py-0.5 text-xs text-red-600" onClick={e => { e.stopPropagation(); handleDeleteSlide(idx); }} title="Delete">âœ•</button>
                          </div>
                        </div>
                      )}
                    </Draggable>
                  ))}
                  {provided.placeholder}
                </div>
              )}
            </Droppable>
          </DragDropContext>
        </div>

        {/* Slide Editor */}
        <div className="flex-1 bg-white rounded-lg shadow-lg p-8" style={{ maxHeight: SLIDE_HEIGHT + 64, overflowY: 'auto' }}>
          <div className="mb-4 flex items-center justify-between">
            <div className="flex items-center gap-2">
              <Link to={`/course/${courseId}`} className="btn-secondary">
                <ArrowLeft className="h-4 w-4 mr-2" /> Back to Course
              </Link>
              <span className="text-lg font-semibold text-gray-700">Slide Editor</span>
            </div>
            <button className="btn-primary">Present</button>
          </div>

          {/* Slide Templates Toolbar */}
          <div className="mb-4 flex gap-2">
            {SLIDE_TEMPLATES.map(t => (
              <button
                key={t.type}
                className={`btn-secondary ${template === t.type ? 'bg-primary-100 text-primary-700' : ''}`}
                onClick={() => setTemplate(t.type)}
              >
                {t.label}
              </button>
            ))}
            <button className="btn-secondary" onClick={() => setSlides(prev => prev.map((s, i) => i === selectedSlide ? { ...s, ...SLIDE_TEMPLATES.find(t => t.type === template).content, type: template } : s))}>Clear</button>
          </div>

          {/* Slide Content Editor */}
          <div className="border rounded-lg p-4 bg-gray-50" style={{ minHeight: SLIDE_HEIGHT, maxHeight: SLIDE_HEIGHT, overflowY: 'auto' }}>
            {(slides[selectedSlide]?.type === 'title' || slides[selectedSlide]?.type === 'basic') && (
              <ReactQuill
                theme="snow"
                value={slides[selectedSlide].content}
                onChange={val => handleSlideChange('content', val)}
                modules={quillModules}
                formats={quillFormats}
                placeholder="Click to add content..."
                style={{ height: SLIDE_HEIGHT - 60, maxHeight: SLIDE_HEIGHT - 60, overflowY: 'auto' }}
              />
            )}
            {slides[selectedSlide]?.type === 'mcq' && (
              <>
                <input
                  className="w-full text-xl font-bold mb-2 border-b border-gray-300 bg-transparent focus:outline-none"
                  placeholder="Click to add question..."
                  value={slides[selectedSlide].question}
                  onChange={e => handleSlideChange('question', e.target.value)}
                />
                <div className="space-y-2 mt-2">
                  {slides[selectedSlide].options.map((opt, i) => (
                    <div key={i} className="flex items-center gap-2">
                      <span className="text-gray-400 select-none">{i + 1}.</span>
                      <input
                        className={`flex-1 border-b border-gray-200 bg-transparent focus:outline-none ${slides[selectedSlide].correct?.includes(i) ? 'font-bold text-primary-700' : ''}`}
                        placeholder={`Option ${i + 1}`}
                        value={opt}
                        onChange={e => {
                          setSlides(prev => prev.map((slide, idx) => idx === selectedSlide ? { ...slide, options: slide.options.map((o, oi) => oi === i ? e.target.value : o) } : slide));
                        }}
                      />
                      <button
                        type="button"
                        className={`btn-secondary px-2 py-1 text-xs ${slides[selectedSlide].correct?.includes(i) ? 'bg-primary-200 font-bold' : ''}`}
                        onClick={() => toggleMCQCorrect(i)}
                        title="Mark as correct answer"
                      >
                        Bold
                      </button>
                    </div>
                  ))}
                  <button
                    type="button"
                    className="btn-secondary mt-2"
                    onClick={() => setSlides(prev => prev.map((slide, idx) => idx === selectedSlide ? { ...slide, options: [...slide.options, ''] } : slide))}
                  >
                    + Add Option
                  </button>
                </div>
              </>
            )}
            {slides[selectedSlide]?.type === 'open' && (
              <>
                <input
                  className="w-full text-xl font-bold mb-2 border-b border-gray-300 bg-transparent focus:outline-none"
                  placeholder="Click to add question..."
                  value={slides[selectedSlide].question}
                  onChange={e => handleSlideChange('question', e.target.value)}
                />
                <div className="mt-4 text-gray-400 italic">(Student will type their answer here)</div>
              </>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default PresentationEditor;
