import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import PresentationService from '../services/PresentationService';
import { useAuth } from '../contexts/AuthContext';
import { useNavigate } from 'react-router-dom';
import { onSnapshot, doc, collection, getDocs, query, orderBy, addDoc, serverTimestamp } from 'firebase/firestore';
import { MessageSquare } from 'lucide-react';

function getRandomCode() {
  return Math.floor(10000 + Math.random() * 90000).toString();
}

const LivePresentationViewer = () => {
  const { courseId } = useParams();
  const navigate = useNavigate();
  const { currentUser } = useAuth();
  const [presentationId, setPresentationId] = useState(null);
  const [presentation, setPresentation] = useState(null);
  const [slides, setSlides] = useState([]);
  const [currentSlide, setCurrentSlide] = useState(0);
  const [audienceMode, setAudienceMode] = useState('enrolledUsers'); // TODO: fetch from Firestore if needed
  const [loadingAudienceMode, setLoadingAudienceMode] = useState(true);
  const [username, setUsername] = useState('');
  const [userId, setUserId] = useState('');
  const [showPrompt, setShowPrompt] = useState(false);
  const [showDiscussion, setShowDiscussion] = useState(false);
  const [commentInput, setCommentInput] = useState("");
  const [comments, setComments] = useState([]); // Local state for now

  useEffect(() => {
    console.log('[LiveViewer] MOUNTED. courseId:', courseId);
    const fetchLive = async () => {
      const liveId = await PresentationService.getLivePresentation(courseId);
      setPresentationId(liveId);
      console.log('[LiveViewer] livePresentationId:', liveId);
      if (liveId) {
        // Real-time listener for presentation document
        const presDocRef = doc(PresentationService.db || require('../firebase/config').db, 'courses', courseId, 'presentations', liveId);
        const unsubPres = onSnapshot(presDocRef, (snap) => {
          if (snap.exists()) {
            const pres = { id: snap.id, ...snap.data() };
            setPresentation(pres);
            setAudienceMode(pres.audienceMode || 'enrolledUsers');
            setLoadingAudienceMode(false);
            console.log('[LiveViewer] [onSnapshot] fetched presentation:', pres);
          }
        });
        // Real-time listener for slides
        const db = PresentationService.db || require('../firebase/config').db;
        const slidesCol = collection(db, 'courses', courseId, 'presentations', liveId, 'slides');
        const slidesQuery = query(slidesCol, orderBy('order', 'asc'));
        const unsubSlides = onSnapshot(slidesQuery, (snap) => {
          const slidesArr = [];
          snap.forEach(doc => slidesArr.push({ id: doc.id, ...doc.data() }));
          setSlides(slidesArr);
          console.log('[LiveViewer] [onSnapshot] fetched slides:', slidesArr);
        });
        // Cleanup
        return () => {
          unsubPres();
          unsubSlides();
        };
      } else {
        setLoadingAudienceMode(false);
      }
    };
    fetchLive();
    // eslint-disable-next-line
  }, [courseId]);

  useEffect(() => {
    if (loadingAudienceMode) return; // Don't redirect until loaded
    if (audienceMode === 'enrolledUsers' && !currentUser) {
      console.log('[LiveViewer] Not authenticated, redirecting to login for enrolledUsers mode');
      navigate('/login', { replace: true });
    }
    if (audienceMode === 'anonymous' && !userId) {
      setShowPrompt(true);
      console.log('[LiveViewer] Anonymous mode, showing username prompt');
    }
  }, [audienceMode, currentUser, userId, navigate, loadingAudienceMode]);

  const handleUsernameSubmit = (e) => {
    e.preventDefault();
    if (username.trim()) {
      setUserId(username.trim() + '_' + getRandomCode());
      setShowPrompt(false);
    }
  };

  // Real-time Firestore sync for comments
  useEffect(() => {
    if (!presentationId || !presentation || !slides.length) return;
    const db = PresentationService.db || require('../firebase/config').db;
    const currentSlide = slides[presentation.currentSlideIndex];
    if (!currentSlide) return;
    const commentsCol = collection(db, 'courses', courseId, 'presentations', presentationId, 'slides', currentSlide.id, 'comments');
    const q = query(commentsCol, orderBy('timePosted', 'asc'));
    const unsub = onSnapshot(q, (snap) => {
      const arr = [];
      snap.forEach(doc => arr.push({ id: doc.id, ...doc.data() }));
      setComments(arr);
    });
    return () => unsub();
  }, [presentationId, presentation, slides, courseId]);

  const handleCommentSubmit = async (e) => {
    e.preventDefault();
    if (!commentInput.trim()) return;
    const db = PresentationService.db || require('../firebase/config').db;
    const currentSlide = slides[presentation.currentSlideIndex];
    if (!currentSlide) return;
    const commentsCol = collection(db, 'courses', courseId, 'presentations', presentationId, 'slides', currentSlide.id, 'comments');
    await addDoc(commentsCol, {
      text: commentInput,
      username: userId,
      likes: 0,
      timePosted: serverTimestamp(),
      groupId: null
    });
    setCommentInput("");
  };

  if (!presentationId) {
    console.warn('[LiveViewer] No live presentationId, rendering fallback');
    return <div className="flex items-center justify-center min-h-screen text-xl text-gray-600">No live presentation is currently being delivered for this course.</div>;
  }

  if (audienceMode === 'anonymous' && showPrompt) {
    console.log('[LiveViewer] Rendering username prompt UI');
    return (
      <div className="flex items-center justify-center min-h-screen bg-gray-50">
        <form onSubmit={handleUsernameSubmit} className="bg-white p-8 rounded shadow-lg flex flex-col items-center">
          <h2 className="text-2xl font-bold mb-4">Enter a username to join</h2>
          <input
            type="text"
            value={username}
            onChange={e => setUsername(e.target.value)}
            className="border px-4 py-2 rounded mb-4 text-lg"
            placeholder="Your name"
            required
          />
          <button type="submit" className="bg-blue-600 text-white px-6 py-2 rounded font-semibold">Join</button>
        </form>
      </div>
    );
  }

  // TODO: Fetch and show slides, poll/interaction UI, etc.
  console.log('[LiveViewer] Rendering main viewer UI. presentation:', presentation, 'audienceMode:', audienceMode, 'userId:', userId);
  return (
    <div className="relative w-screen h-screen bg-black overflow-hidden">
      {/* Username at top-right */}
      <div className="absolute top-4 right-8 z-20 text-white text-sm bg-black/60 px-3 py-1 rounded-full shadow">
        {userId}
      </div>
      {/* Discussion Icon */}
      <button
        className="absolute top-4 right-24 z-30 bg-white bg-opacity-80 rounded-full p-2 shadow hover:bg-primary-100"
        title="Open discussion"
        onClick={() => setShowDiscussion(true)}
      >
        <MessageSquare className="w-6 h-6 text-primary-600" />
      </button>
      {/* Slide content/image centered and scaled */}
      <div className="flex items-center justify-center w-full h-full">
        {slides.length === 0 ? (
          <div className="text-gray-400 text-2xl">No slides found.</div>
        ) : (
          presentation && typeof presentation.currentSlideIndex === 'number' && slides[presentation.currentSlideIndex] ? (
            <div className="w-full h-full flex flex-col items-center justify-center">
              {/* If imageUrl, show image full size, else show text */}
              {slides[presentation.currentSlideIndex].imageUrl ? (
                <img
                  src={slides[presentation.currentSlideIndex].imageUrl}
                  alt="Slide"
                  className="object-contain w-full h-full max-h-[90vh] max-w-[90vw] rounded shadow-lg bg-white"
                  style={{ background: 'white' }}
                />
              ) : (
                <div className="flex items-center justify-center w-full h-full">
                  <div className="text-white text-3xl font-semibold text-center px-8 py-4 bg-black/60 rounded-lg shadow">
                    {slides[presentation.currentSlideIndex].content || slides[presentation.currentSlideIndex].text || ''}
                  </div>
                </div>
              )}
              {/* Overlay for polls/interactions (placeholder) */}
              <div className="absolute inset-0 flex items-center justify-center pointer-events-none z-30">
                {/* TODO: Polls/interactions overlay goes here */}
              </div>
            </div>
          ) : (
            <div className="text-gray-400 text-2xl">Slide will appear here</div>
          )
        )}
      </div>
      {/* Discussion Overlay */}
      {showDiscussion && (
        <div className="fixed inset-0 z-40 flex items-center justify-center bg-black bg-opacity-60">
          <div className="absolute inset-0" onClick={() => setShowDiscussion(false)} />
          <div className="relative flex w-full h-full z-50">
            {/* Left: Grouping area (placeholder) */}
            <div className="flex-1 bg-gray-100 flex flex-col items-center justify-center border-r border-gray-300">
              <div className="text-gray-400 text-lg">(Grouping area placeholder)</div>
            </div>
            {/* Right: Chat/comments */}
            <div className="w-[400px] max-w-full bg-white flex flex-col h-full shadow-xl">
              {/* Header */}
              <div className="flex items-center justify-between px-4 py-3 border-b">
                <div className="font-semibold text-lg">Slide Discussion</div>
                <button onClick={() => setShowDiscussion(false)} className="text-gray-500 hover:text-primary-600 text-xl">&times;</button>
              </div>
              {/* Chat panel */}
              <div className="flex-1 overflow-y-auto p-4 space-y-3">
                {comments.length === 0 ? (
                  <div className="text-gray-400 text-center">No messages yet.</div>
                ) : (
                  comments.map((c) => (
                    <div key={c.id} className="bg-gray-100 rounded px-3 py-2 flex flex-col">
                      <div className="flex items-center justify-between mb-1">
                        <span className="font-semibold text-sm text-primary-700">{c.username}</span>
                        <span className="text-xs text-gray-400">{new Date(c.timePosted).toLocaleTimeString()}</span>
                      </div>
                      <div className="text-gray-800 text-sm">{c.text}</div>
                    </div>
                  ))
                )}
              </div>
              {/* Input box */}
              <form className="flex items-center border-t px-4 py-3" onSubmit={handleCommentSubmit}>
                <input
                  type="text"
                  className="flex-1 border rounded px-3 py-2 mr-2 focus:outline-none focus:border-primary-500"
                  placeholder="Type a comment..."
                  value={commentInput}
                  onChange={e => setCommentInput(e.target.value)}
                />
                <button type="submit" className="bg-primary-600 text-white px-4 py-2 rounded font-semibold disabled:opacity-50" disabled={!commentInput.trim()}>
                  Send
                </button>
              </form>
            </div>
          </div>
        </div>
      )}
      {/* Slide number at bottom-right */}
      {slides.length > 0 && presentation && typeof presentation.currentSlideIndex === 'number' && (
        <div className="absolute bottom-6 right-8 z-20 text-white text-lg bg-black/60 px-4 py-2 rounded-full shadow">
          {presentation.currentSlideIndex + 1} / {slides.length}
        </div>
      )}
    </div>
  );
};

export default LivePresentationViewer;
