import { collection, addDoc, getDocs, query, orderBy, serverTimestamp, doc, deleteDoc, updateDoc, getDoc, setDoc } from 'firebase/firestore';
import { db } from '../firebase/config';

class PresentationService {
  // Get a reference to the presentations subcollection for a course
  getPresentationsCollection(courseId) {
    return collection(db, 'courses', courseId, 'presentations');
  }

  // Create a new presentation
  async createPresentation(courseId, title, ownerId) {
    try {
      console.log('[PresentationService] Creating presentation:', { courseId, title, ownerId });
      const presentationsCol = this.getPresentationsCollection(courseId);
      const docRef = await addDoc(presentationsCol, {
        title,
        ownerId,
        createdAt: serverTimestamp(),
        updatedAt: serverTimestamp(),
        slideCount: 0,
        audienceMode: 'enrolledUsers' // Default mode
      });
      console.log('[PresentationService] Presentation created with ID:', docRef.id);
      return docRef;
    } catch (err) {
      console.error('[PresentationService] Error creating presentation:', err);
      throw err;
    }
  }

  // Get all presentations for a course
  async getPresentations(courseId) {
    try {
      console.log('[PresentationService] Fetching presentations for course:', courseId);
      const presentationsCol = this.getPresentationsCollection(courseId);
      const q = query(presentationsCol, orderBy('createdAt', 'asc'));
      const snapshot = await getDocs(q);
      const result = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
      console.log('[PresentationService] Presentations fetched:', result);
      return result;
    } catch (err) {
      console.error('[PresentationService] Error fetching presentations:', err);
      throw err;
    }
  }

  // Delete a presentation
  async deletePresentation(courseId, presentationId) {
    try {
      const presentationDoc = doc(db, 'courses', courseId, 'presentations', presentationId);
      await deleteDoc(presentationDoc);
      console.log('[PresentationService] Presentation deleted:', presentationId);
    } catch (err) {
      console.error('[PresentationService] Error deleting presentation:', err);
      throw err;
    }
  }

  // Add a slide to a presentation
  async addSlide(courseId, presentationId, slideData) {
    try {
      console.log('[PresentationService] Adding slide to presentation:', { courseId, presentationId, slideData });
      const slidesCol = collection(db, 'courses', courseId, 'presentations', presentationId, 'slides');
      const docRef = await addDoc(slidesCol, {
        ...slideData,
        createdAt: serverTimestamp(),
        updatedAt: serverTimestamp()
      });
      
      // Update presentation slide count
      const presentationDoc = doc(db, 'courses', courseId, 'presentations', presentationId);
      await updateDoc(presentationDoc, {
        updatedAt: serverTimestamp()
      });
      
      console.log('[PresentationService] Slide added with ID:', docRef.id);
      return docRef;
    } catch (err) {
      console.error('[PresentationService] Error adding slide:', err);
      throw err;
    }
  }

  // Set the live presentation for a course
  async setLivePresentation(courseId, presentationId) {
    const courseDoc = doc(db, 'courses', courseId);
    await updateDoc(courseDoc, { livePresentation: presentationId });
  }

  // Get the live presentation for a course
  async getLivePresentation(courseId) {
    const courseDoc = doc(db, 'courses', courseId);
    const courseSnap = await getDoc(courseDoc);
    if (courseSnap.exists()) {
      return courseSnap.data().livePresentation || null;
    }
    return null;
  }

  // Get a single presentation by courseId and presentationId
  async getPresentation(presentationId, courseId) {
    try {
      console.log('[PresentationService] getPresentation called with:', { courseId, presentationId });
      const presentationDoc = doc(db, 'courses', courseId, 'presentations', presentationId);
      const snap = await getDoc(presentationDoc);
      if (snap.exists()) {
        console.log('[PresentationService] Presentation found:', snap.data());
        return { id: snap.id, ...snap.data() };
      } else {
        console.warn('[PresentationService] Presentation does not exist:', { courseId, presentationId });
        return null;
      }
    } catch (err) {
      console.error('[PresentationService] Error fetching presentation:', err, { courseId, presentationId });
      throw err;
    }
  }

  // Set the audience mode for a presentation
  async setAudienceMode(courseId, presentationId, mode) {
    try {
      const presentationDoc = doc(db, 'courses', courseId, 'presentations', presentationId);
      const payload = { audienceMode: mode, updatedAt: serverTimestamp() };
      console.log('[setAudienceMode] Updating doc:', presentationDoc.path, 'with:', payload);
      await updateDoc(presentationDoc, payload);
      console.log('[PresentationService] audienceMode updated:', { courseId, presentationId, mode });
    } catch (err) {
      console.error('[PresentationService] Error updating audienceMode:', err, { courseId, presentationId, mode });
      throw err;
    }
  }
}

export default new PresentationService();
