import React, { useState, useEffect, useCallback, useMemo, useRef } from 'react';
import { doc, getDoc, updateDoc, setDoc, collection, getDocs, query, orderBy, onSnapshot, addDoc, serverTimestamp } from 'firebase/firestore';
import { db } from '../firebase/config';
import { PresentationToolbar } from './PresentationToolbar';
import { SlidesSidebar } from './SlidesSidebar';
import { SlideCanvas } from './SlideCanvas';
import { ToastContainer } from '../components/Toast';
import { useAuth } from '../contexts/AuthContext';
import PresentationService from '../services/PresentationService';
import { ThumbsUp, MessageSquare } from 'lucide-react';

// Types (replace with import from types/presentation if available)
const initialPresentation = {
  id: 'presentation-1',
  title: 'Untitled Presentation',
  slides: [
    {
      id: 'slide-1',
      title: 'Welcome Slide',
      elements: [],
    },
  ],
  createdAt: new Date(),
  updatedAt: new Date(),
  currentSlideIndex: 0,
};

export default function PresentationEditorV3({ courseId, presentationId = 'demo-presentation', title }) {
  const { currentUser } = useAuth();
  // All hooks must be at the top, before any return or conditional
  const [presentation, setPresentation] = useState(initialPresentation);
  const [editorState, setEditorState] = useState({
    selectedElement: null,
    tool: 'select',
    isPresenting: false,
    zoom: 100,
  });
  const [isLoading, setIsLoading] = useState(true);
  const [isSaving, setIsSaving] = useState(false);
  const [toasts, setToasts] = useState([]);
  const [audienceMode, setAudienceMode] = useState('enrolledUsers');
  // --- Discussion state/hooks for instructor live mode (copied from viewer side) ---
  const [showDiscussion, setShowDiscussion] = useState(false);
  const [commentInput, setCommentInput] = useState("");
  const [comments, setComments] = useState([]);
  // Real-time Firestore sync for comments (current slide)
  useEffect(() => {
    if (!editorState.isPresenting || !presentationId || !courseId || !Array.isArray(presentation.slides) || !presentation.slides.length) return;
    const currentSlide = presentation.slides[presentation.currentSlideIndex];
    if (!currentSlide) return;
    const commentsCol = collection(db, 'courses', courseId, 'presentations', presentationId, 'slides', currentSlide.id, 'comments');
    const q = query(commentsCol, orderBy('timePosted', 'asc'));
    const unsub = onSnapshot(q, (snap) => {
      const arr = [];
      snap.forEach(doc => arr.push({ id: doc.id, ...doc.data() }));
      setComments(arr);
    });
    return () => unsub();
  }, [editorState.isPresenting, presentationId, courseId, presentation.slides, presentation.currentSlideIndex]);

  // Toast management
  const addToast = useCallback((message, type = 'info', duration = 3000) => {
    const id = Date.now().toString();
    setToasts(prev => [...prev, { id, message, type, duration }]);
  }, []);

  const removeToast = useCallback((id) => {
    setToasts(prev => prev.filter(toast => toast.id !== id));
  }, []);

  // Load presentation from Firestore
  useEffect(() => {
    const loadPresentation = async () => {
      try {
        console.log('Loading presentation:', presentationId);
        const docRef = doc(db, 'courses', courseId, 'presentations', presentationId);
        const docSnap = await getDoc(docRef);
        let slides = [];
        // Fetch slides from subcollection
        const slidesCol = collection(db, 'courses', courseId, 'presentations', presentationId, 'slides');
        const slidesQuery = query(slidesCol, orderBy('order'));
        const slidesSnap = await getDocs(slidesQuery);
        slides = slidesSnap.docs.map(doc => {
          const data = doc.data();
          return {
            id: doc.id,
            ...data,
            elements: Array.isArray(data.elements) ? data.elements : [],
          };
        });
        console.log('[PresentationEditorV3] Fetched slides from subcollection:', slides);
        if (docSnap.exists()) {
          const data = docSnap.data();
          setPresentation({
            ...data,
            slides,
            createdAt: data.createdAt?.toDate() || new Date(),
            updatedAt: data.updatedAt?.toDate() || new Date(),
          });
        } else {
          if (!currentUser) throw new Error('Not authenticated');
          console.log('No presentation found, creating new one');
          await setDoc(docRef, {
            ...initialPresentation,
            slides: [],
            id: presentationId,
            createdAt: new Date(),
            updatedAt: new Date(),
            ownerId: currentUser.uid,
            courseId: courseId || null,
          });
          setPresentation({ ...initialPresentation, slides: [], id: presentationId, ownerId: currentUser.uid, courseId: courseId || null });
        }
      } catch (error) {
        console.error('Error loading presentation:', error);
        if (error.code === 'permission-denied' || error.message.includes('permission')) {
          addToast('You do not have permission to access this presentation.', 'error', 6000);
        } else {
          addToast('Failed to load presentation', 'error');
        }
      } finally {
        setIsLoading(false);
      }
    };
    if (currentUser) loadPresentation();
  }, [presentationId, addToast, currentUser, courseId]);

  // Add logging after fetching presentation and slides
  useEffect(() => {
    if (presentation) {
      console.log('[PresentationEditorV3] Loaded presentation:', presentation);
      if (presentation.slides) {
        console.log('[PresentationEditorV3] Slides array:', presentation.slides);
        presentation.slides.forEach((slide, idx) => {
          console.log(`[PresentationEditorV3] Slide ${idx} imageUrl:`, slide.imageUrl);
        });
      } else {
        console.log('[PresentationEditorV3] No slides found in presentation.');
      }
    }
  }, [presentation]);

  // Auto-save with debouncing
  const saveTimeoutRef = React.useRef(null);
  const savePresentation = useCallback(async (presentationData) => {
    try {
      if (!currentUser) throw new Error('Not authenticated');
      console.log('Saving presentation:', presentationData);
      setIsSaving(true);

      const docRef = doc(db, 'courses', courseId, 'presentations', presentationId);
      await updateDoc(docRef, {
        ...presentationData,
        updatedAt: new Date(),
        ownerId: currentUser.uid,
        courseId: courseId || null,
      });

      console.log('Presentation saved successfully');
      addToast('Presentation saved', 'success', 2000);
    } catch (error) {
      console.error('Error saving presentation:', error);
      if (error.code === 'permission-denied' || error.message.includes('permission')) {
        addToast('You do not have permission to save this presentation.', 'error', 6000);
      } else {
        addToast('Failed to save presentation', 'error');
      }
    } finally {
      setIsSaving(false);
    }
  }, [presentationId, addToast, currentUser, courseId]);

  // Debounced auto-save
  useEffect(() => {
    if (saveTimeoutRef.current) {
      clearTimeout(saveTimeoutRef.current);
    }

    if (!isLoading) {
      saveTimeoutRef.current = setTimeout(() => {
        savePresentation(presentation);
      }, 2000); // 2 second delay
    }

    return () => {
      if (saveTimeoutRef.current) {
        clearTimeout(saveTimeoutRef.current);
      }
    };
  }, [presentation, savePresentation, isLoading]);

  // Manual save handler
  const handleSave = useCallback(async () => {
    if (saveTimeoutRef.current) {
      clearTimeout(saveTimeoutRef.current);
    }
    await savePresentation(presentation);
  }, [presentation, savePresentation]);

  // Slide management handlers
  const handleAddSlide = useCallback(() => {
    const newSlide = {
      id: `slide-${Date.now()}`,
      title: `Slide ${presentation.slides.length + 1}`,
      elements: [],
    };

    setPresentation(prev => ({
      ...prev,
      slides: [...prev.slides, newSlide],
      currentSlideIndex: prev.slides.length,
    }));

    addToast('New slide added', 'success', 2000);
  }, [presentation.slides.length, addToast]);

  const handleDuplicateSlide = useCallback((index) => {
    const slideToDuplicate = presentation.slides[index];
    const newSlide = {
      ...slideToDuplicate,
      id: `slide-${Date.now()}`,
      title: `${slideToDuplicate.title} (Copy)`,
    };

    const newSlides = [...presentation.slides];
    newSlides.splice(index + 1, 0, newSlide);

    setPresentation(prev => ({
      ...prev,
      slides: newSlides,
      currentSlideIndex: index + 1,
    }));

    addToast('Slide duplicated', 'success', 2000);
  }, [presentation.slides, addToast]);

  const handleDeleteSlide = useCallback((index) => {
    if (presentation.slides.length <= 1) {
      addToast('Cannot delete the last slide', 'warning');
      return;
    }

    const newSlides = presentation.slides.filter((_, i) => i !== index);
    const newCurrentIndex = Math.min(
      presentation.currentSlideIndex,
      newSlides.length - 1
    );

    setPresentation(prev => ({
      ...prev,
      slides: newSlides,
      currentSlideIndex: newCurrentIndex,
    }));

    addToast('Slide deleted', 'success', 2000);
  }, [presentation.slides, presentation.currentSlideIndex, addToast]);

  const handleAddSlideAfter = useCallback((index) => {
    const newSlide = {
      id: `slide-${Date.now()}`,
      title: `Slide ${presentation.slides.length + 1}`,
      elements: [],
    };

    const newSlides = [...presentation.slides];
    newSlides.splice(index + 1, 0, newSlide);

    setPresentation(prev => ({
      ...prev,
      slides: newSlides,
      currentSlideIndex: index + 1,
    }));

    addToast('New slide added', 'success', 2000);
  }, [presentation.slides.length, addToast]);

  // Element management handlers
  const handleElementSelect = useCallback((elementId) => {
    setEditorState(prev => ({ ...prev, selectedElement: elementId }));
  }, []);

  const handleElementUpdate = useCallback((elementId, updates) => {
    setPresentation(prev => {
      const newSlides = [...prev.slides];
      const currentSlide = newSlides[prev.currentSlideIndex];
      const elementIndex = currentSlide.elements.findIndex(el => el.id === elementId);

      if (elementIndex !== -1) {
        newSlides[prev.currentSlideIndex] = {
          ...currentSlide,
          elements: currentSlide.elements.map((el, index) =>
            index === elementIndex ? { ...el, ...updates } : el
          ),
        };
      }

      return { ...prev, slides: newSlides };
    });
  }, []);

  const handleElementAdd = useCallback((elementData) => {
    const newElement = {
      id: `element-${Date.now()}`,
      ...elementData,
    };

    setPresentation(prev => {
      const newSlides = [...prev.slides];
      const currentSlide = newSlides[prev.currentSlideIndex];

      newSlides[prev.currentSlideIndex] = {
        ...currentSlide,
        elements: [...currentSlide.elements, newElement],
      };

      return { ...prev, slides: newSlides };
    });

    // Auto-select the new element
    setEditorState(prev => ({ ...prev, selectedElement: newElement.id }));
    addToast('Element added', 'success', 1500);
  }, [addToast]);

  const handleElementDelete = useCallback((elementId) => {
    setPresentation(prev => {
      const newSlides = [...prev.slides];
      const currentSlide = newSlides[prev.currentSlideIndex];

      newSlides[prev.currentSlideIndex] = {
        ...currentSlide,
        elements: currentSlide.elements.filter(el => el.id !== elementId),
      };

      return { ...prev, slides: newSlides };
    });

    setEditorState(prev => ({ ...prev, selectedElement: null }));
    addToast('Element deleted', 'success', 1500);
  }, [addToast]);

  // Other handlers
  const handleToolChange = useCallback((tool) => {
    setEditorState(prev => ({ ...prev, tool }));
  }, []);

  const handleTitleChange = useCallback((title) => {
    setPresentation(prev => ({ ...prev, title }));
  }, []);

  // Ensure every slide navigation triggers Firestore update
  const handleSlideSelect = useCallback((index) => {
    setPresentation(prev => ({ ...prev, currentSlideIndex: index }));
    setEditorState(prev => ({ ...prev, selectedElement: null }));
    // Log for debug
    console.log('[SYNC] handleSlideSelect called, new index:', index);
  }, []);

  const handlePresent = useCallback(async () => {
    setEditorState(prev => ({ ...prev, isPresenting: true }));
    addToast('Starting presentation...', 'info');
    try {
      await PresentationService.setLivePresentation(courseId, presentationId);
      addToast('Presentation is now live!', 'success');
    } catch (err) {
      addToast('Failed to set presentation live: ' + (err.message || err), 'error');
      console.error('[PresentationEditorV3] Error setting presentation live:', err);
    }
  }, [addToast, courseId, presentationId]);

  // Remove auto-save useEffect
  // Add slide change handler
  const handleSlideChange = useCallback((field, value) => {
    setPresentation(prev => {
      const newSlides = [...prev.slides];
      const idx = prev.currentSlideIndex;
      if (!newSlides[idx]) return prev;
      newSlides[idx] = { ...newSlides[idx], [field]: value };
      return { ...prev, slides: newSlides };
    });
  }, []);

  // Update currentSlideIndex in Firestore when it changes
  useEffect(() => {
    if (!presentationId || !courseId || !currentUser) return;
    const updateCurrentSlideIndex = async () => {
      try {
        const docRef = doc(db, 'courses', courseId, 'presentations', presentationId);
        console.log('[SYNC] Writing currentSlideIndex to Firestore:', presentation.currentSlideIndex);
        await updateDoc(docRef, { currentSlideIndex: presentation.currentSlideIndex });
      } catch (err) {
        console.error('[PresentationEditorV3] Error updating currentSlideIndex:', err);
      }
    };
    updateCurrentSlideIndex();
  }, [presentation.currentSlideIndex, presentationId, courseId, currentUser]);

  // Find selected element from current slide
  const selectedElement = React.useMemo(() => {
    const slide = presentation.slides[presentation.currentSlideIndex];
    if (!slide) return null;
    return slide.elements.find(el => el.id === editorState.selectedElement) || null;
  }, [presentation, editorState.selectedElement]);

  // Compute formatting state for toolbar
  const formattingState = React.useMemo(() => {
    if (!selectedElement || selectedElement.type !== 'text') return {};
    const style = selectedElement.style || {};
    return {
      bold: style.fontWeight === 'bold',
      italic: style.fontStyle === 'italic',
      underline: style.textDecoration === 'underline',
      fontSize: style.fontSize || 16,
      color: style.color || '#000000',
      align: style.textAlign || 'left',
    };
  }, [selectedElement]);

  // Handler for formatting actions
  const handleFormat = useCallback((action, value) => {
    if (!selectedElement || selectedElement.type !== 'text') return;
    setPresentation(prev => {
      const newSlides = [...prev.slides];
      const slide = newSlides[prev.currentSlideIndex];
      const idx = slide.elements.findIndex(el => el.id === selectedElement.id);
      if (idx === -1) return prev;
      const el = slide.elements[idx];
      const style = { ...el.style };
      switch (action) {
        case 'bold':
          style.fontWeight = style.fontWeight === 'bold' ? 'normal' : 'bold';
          break;
        case 'italic':
          style.fontStyle = style.fontStyle === 'italic' ? 'normal' : 'italic';
          break;
        case 'underline':
          style.textDecoration = style.textDecoration === 'underline' ? 'none' : 'underline';
          break;
        case 'fontSize':
          style.fontSize = value || 20; // Default to 20 if not provided
          break;
        case 'color':
          style.color = value || '#000000';
          break;
        case 'align':
          style.textAlign = value;
          break;
        default:
          break;
      }
      slide.elements[idx] = { ...el, style };
      return { ...prev, slides: newSlides };
    });
  }, [selectedElement]);

  // Loading state
  if (isLoading) {
    return (
      <div className="h-screen flex items-center justify-center bg-gray-100">
        <div className="text-center">
          <div className="w-8 h-8 border-4 border-blue-500 border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
          <p className="text-gray-600">Loading presentation...</p>
        </div>
      </div>
    );
  }

  // Comment submit
  const handleCommentSubmit = async (e) => {
    e.preventDefault();
    if (!commentInput.trim() || !currentUser) return;
    const currentSlide = presentation.slides[presentation.currentSlideIndex];
    if (!currentSlide) return;
    const commentsCol = collection(db, 'courses', courseId, 'presentations', presentationId, 'slides', currentSlide.id, 'comments');
    await addDoc(commentsCol, {
      text: commentInput,
      username: currentUser.displayName || currentUser.email || currentUser.uid,
      likedBy: {},
      timePosted: serverTimestamp(),
      groupId: null
    });
    setCommentInput("");
  };
  // Like handler (likedBy map logic)
  const handleLike = async (comment) => {
    let likeUserId;
    if (currentUser) {
      likeUserId = currentUser.uid;
    } else {
      // Anonymous: use stable anonId from localStorage
      likeUserId = localStorage.getItem('anonId');
      if (!likeUserId) {
        likeUserId = 'anon_' + Math.random().toString(36).substr(2, 9);
        localStorage.setItem('anonId', likeUserId);
      }
    }
    if (!likeUserId) return;
    const currentSlide = presentation.slides[presentation.currentSlideIndex];
    if (!currentSlide) return;
    if (comment.likedBy && comment.likedBy[likeUserId]) return;
    const newLikedBy = { ...(comment.likedBy || {}) };
    newLikedBy[likeUserId] = true;
    const commentRef = doc(db, 'courses', courseId, 'presentations', presentationId, 'slides', currentSlide.id, 'comments', comment.id);
    const payload = { likedBy: newLikedBy };
    console.log('[handleLike] Updating comment with payload:', payload);
    await updateDoc(commentRef, payload);
  };

  const PresentationFullScreen = React.memo(function PresentationFullScreen({ slides, currentIndex, onClose, onPrev, onNext, showDiscussion, setShowDiscussion, comments, commentInput, setCommentInput, handleCommentSubmit, handleLike, currentUser }) {
    const total = slides.length;
    const slide = slides[currentIndex] || {};
    const containerRef = useRef();
    // Only use useRef and useEffect for keydown events here. No useState/useEffect for discussion logic.
    useEffect(() => {
      const handleKeyDown = (e) => {
        if (e.key === 'ArrowLeft') onPrev();
        if (e.key === 'ArrowRight') onNext();
        if (e.key === 'Escape') onClose();
      };
      window.addEventListener('keydown', handleKeyDown);
      return () => window.removeEventListener('keydown', handleKeyDown);
    }, [onPrev, onNext, onClose]);

    return (
      <div ref={containerRef} className="fixed inset-0 z-50 bg-black flex flex-col items-center justify-center" style={{ minHeight: '100vh', minWidth: '100vw' }}>
        {/* Username at top-right */}
        <div className="absolute top-4 right-8 z-20 text-white text-sm bg-black/60 px-3 py-1 rounded-full shadow">
          {currentUser && (currentUser.displayName || currentUser.email || currentUser.uid)}
        </div>
        {/* Discussion Icon */}
        <button
          className="absolute top-4 right-24 z-30 bg-white bg-opacity-80 rounded-full p-2 shadow hover:bg-primary-100"
          title="Open discussion"
          onClick={e => { e.stopPropagation(); console.log('[DiscussionIcon] Opening discussion overlay'); setShowDiscussion(true); }}
        >
          <MessageSquare className="w-6 h-6 text-primary-600" />
        </button>
        {/* Close button (X) at bottom right of slide view */}
        <button onClick={e => { e.stopPropagation(); onClose(); }} className="absolute bottom-8 right-8 text-white text-3xl font-bold bg-black bg-opacity-40 rounded px-3 py-1 hover:bg-opacity-70 z-50"></button>
        {/* Previous arrow */}
        <button onClick={onPrev} className="absolute left-4 top-1/2 -translate-y-1/2 text-white text-5xl font-bold bg-black bg-opacity-40 rounded-full px-3 py-1 hover:bg-opacity-70" style={{zIndex: 10}} aria-label="Previous slide">&#8592;</button>
        {/* Next arrow */}
        <button onClick={onNext} className="absolute right-4 top-1/2 -translate-y-1/2 text-white text-5xl font-bold bg-black bg-opacity-40 rounded-full px-3 py-1 hover:bg-opacity-70" style={{zIndex: 10}} aria-label="Next slide">&#8594;</button>
        {/* Slide content */}
        <div className="flex flex-col items-center justify-center w-full h-full">
          <div className="flex items-center justify-center" style={{ width: '96vw', height: '96vh', maxWidth: '100vw', maxHeight: '100vh', padding: 0, margin: 0 }}>
            {slide.imageUrl ? (
              <img src={slide.imageUrl} alt={slide.title || 'Slide'} style={{ width: '100%', height: '100%', objectFit: 'contain', background: '#222' }} />
            ) : (
              <div className="bg-white w-full h-full flex flex-col items-center justify-center">
                <h2 className="text-4xl font-bold mb-4">{slide.title || 'Untitled Slide'}</h2>
                <div className="text-lg text-gray-700">{slide.content || ''}</div>
              </div>
            )}
          </div>
          {/* Slide number overlay */}
          <div className="absolute bottom-8 left-1/2 -translate-x-1/2 bg-black bg-opacity-60 text-white px-4 py-2 rounded text-lg font-semibold">
            Slide {currentIndex + 1} / {total}
          </div>
        </div>
      </div>
    );
  });

  // Audience mode change handler
  const handleAudienceModeChange = async (newMode) => {
    try {
      await PresentationService.setAudienceMode(courseId, presentationId, newMode);
      setAudienceMode(newMode);
      addToast(`Audience mode set to ${newMode === 'anonymous' ? 'Anonymous' : 'Enrolled Users'}`, 'success');
    } catch (err) {
      addToast('Failed to update audience mode', 'error');
      console.error('[PresentationEditorV3] Error updating audience mode:', err);
    }
  };

  // Debug log for overlay rendering
  console.log('[OverlayRender] showDiscussion:', showDiscussion, 'isPresenting:', editorState.isPresenting);
  return (
    <div className="h-screen flex flex-col">
      {/* Toolbar */}
      <PresentationToolbar
        editorState={editorState}
        onToolChange={handleToolChange}
        onSave={handleSave}
        onPresent={handlePresent}
        onUndo={() => {}} // TODO: Implement undo/redo
        onRedo={() => {}}
        presentationTitle={presentation.title}
        onTitleChange={handleTitleChange}
        isSaving={isSaving}
        selectedElement={selectedElement}
        formattingState={formattingState}
        onFormat={handleFormat}
        audienceMode={audienceMode}
        onAudienceModeChange={handleAudienceModeChange}
      />
      <div className="flex-1 flex">
        {/* Sidebar */}
        <div className="h-full overflow-y-auto" style={{ maxHeight: 'calc(100vh - 64px)', minWidth: 120 }}>
          <SlidesSidebar
            slides={presentation.slides}
            currentSlideIndex={presentation.currentSlideIndex}
            onSlideSelect={handleSlideSelect}
            onAddSlide={handleAddSlide}
            onDuplicateSlide={handleDuplicateSlide}
            onDeleteSlide={handleDeleteSlide}
            onAddSlideAfter={handleAddSlideAfter}
          />
        </div>
        {/* Canvas */}
        <SlideCanvas
          slide={presentation.slides[presentation.currentSlideIndex]}
          editorState={editorState}
          onElementSelect={handleElementSelect}
          onElementUpdate={handleElementUpdate}
          onElementAdd={handleElementAdd}
          onElementDelete={handleElementDelete}
          onSlideChange={handleSlideChange}
          isLive={editorState.isPresenting}
        />
      </div>
      {/* Toast notifications */}
      <ToastContainer toasts={toasts} removeToast={removeToast} />
      {editorState.isPresenting && (
        <PresentationFullScreen
          slides={presentation.slides}
          currentIndex={presentation.currentSlideIndex}
          onClose={() => setEditorState(prev => ({ ...prev, isPresenting: false }))}
          onPrev={() => setPresentation(prev => ({ ...prev, currentSlideIndex: Math.max(0, prev.currentSlideIndex - 1) }))}
          onNext={() => setPresentation(prev => ({ ...prev, currentSlideIndex: Math.min(presentation.slides.length - 1, prev.currentSlideIndex + 1) }))}
          showDiscussion={showDiscussion}
          setShowDiscussion={setShowDiscussion}
          comments={comments}
          commentInput={commentInput}
          setCommentInput={setCommentInput}
          handleCommentSubmit={handleCommentSubmit}
          handleLike={handleLike}
          currentUser={currentUser}
        />
      )}
      {/* Discussion Overlay rendered at the top level, not inside PresentationFullScreen */}
      {showDiscussion && (
        <div className="fixed inset-0 z-[9999] flex items-center justify-center bg-black bg-opacity-60" style={{pointerEvents: 'auto'}}>
          <div className="absolute inset-0" onClick={e => { e.stopPropagation(); setShowDiscussion(false); }} />
          <div className="relative flex w-full h-full z-70">
            {/* Left: Grouping area (placeholder) */}
            <div className="flex-1 bg-gray-100 flex flex-col items-center justify-center border-r border-gray-300">
              <div className="text-gray-400 text-lg">(Grouping area placeholder)</div>
            </div>
            {/* Right: Chat/comments */}
            <div className="w-[400px] max-w-full bg-white flex flex-col h-full shadow-xl relative">
              {/* Header */}
              <div className="flex items-center justify-between px-4 py-3 border-b">
                <div className="font-semibold text-lg">Slide Discussion</div>
                {/* X button to close overlay */}
                <button
                  className="ml-2 text-gray-500 hover:text-red-600 text-2xl font-bold focus:outline-none"
                  onClick={e => { e.stopPropagation(); setShowDiscussion(false); }}
                  title="Close discussion"
                  aria-label="Close discussion"
                ></button>
              </div>
              {/* Chat panel */}
              <div className="flex-1 overflow-y-auto p-4 space-y-3">
                {comments.length === 0 ? (
                  <div className="text-gray-400 text-center">No messages yet.</div>
                ) : (
                  comments.map((c) => {
                    const likeUserId = currentUser ? currentUser.uid : '';
                    const alreadyLiked = c.likedBy && likeUserId && c.likedBy[likeUserId];
                    const likeCount = c.likedBy ? Object.keys(c.likedBy).length : 0;
                    return (
                      <div
                        key={c.id}
                        className="bg-gray-100 rounded px-3 py-2 flex items-center group"
                        title={c.username}
                      >
                        {/* Like icon and count */}
                        <button
                          className={`flex items-center mr-3 text-gray-500 hover:text-primary-600 focus:outline-none ${alreadyLiked ? 'opacity-50 cursor-not-allowed' : ''}`}
                          onClick={() => handleLike(c)}
                          tabIndex={0}
                          aria-label="Like comment"
                          disabled={alreadyLiked}
                        >
                          <ThumbsUp className="w-5 h-5 mr-1" />
                          <span className="text-sm font-semibold">{likeCount}</span>
                        </button>
                        {/* Comment text */}
                        <span className="text-gray-800 text-sm" style={{ cursor: 'pointer' }}>
                          {c.text}
                        </span>
                      </div>
                    );
                  })
                )}
              </div>
              {/* Input box */}
              <form className="flex items-center border-t px-4 py-3" onSubmit={handleCommentSubmit}>
                <input
                  type="text"
                  className="flex-1 border rounded px-3 py-2 mr-2 focus:outline-none focus:border-primary-500"
                  placeholder="Type a comment..."
                  value={commentInput}
                  onChange={e => setCommentInput(e.target.value)}
                />
                <button type="submit" className="bg-primary-600 text-white px-4 py-2 rounded font-semibold disabled:opacity-50" disabled={!commentInput.trim()}>
                  Send
                </button>
              </form>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
