import React, { useEffect, useState } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import { Loader, ArrowLeft, Plus } from 'lucide-react';

// Placeholder for slide templates
const SLIDE_TEMPLATES = [
  { type: 'title', label: 'Title Template', content: { title: '', subtitle: '' } },
  { type: 'basic', label: 'Basic Template', content: { title: '', body: '' } },
  { type: 'mcq', label: 'MC Template', content: { question: '', options: ['', '', '', ''], correct: 0 } },
];

const PresentationEditor = () => {
  const { courseId, presentationId } = useParams();
  const navigate = useNavigate();
  const [slides, setSlides] = useState([]);
  const [selectedSlide, setSelectedSlide] = useState(0);
  const [loading, setLoading] = useState(false);
  const [template, setTemplate] = useState('title');

  // Placeholder: fetch slides from Firestore in real app
  useEffect(() => {
    // TODO: fetch slides for this presentation
    setSlides([{ type: 'title', title: '', subtitle: '' }]);
    setSelectedSlide(0);
  }, [presentationId]);

  const handleAddSlide = (type) => {
    const templateObj = SLIDE_TEMPLATES.find(t => t.type === type);
    setSlides(prev => [...prev, { ...templateObj.content, type }]);
    setSelectedSlide(slides.length);
  };

  const handleSlideChange = (field, value) => {
    setSlides(prev => prev.map((slide, idx) => idx === selectedSlide ? { ...slide, [field]: value } : slide));
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-primary-50 to-secondary-50 py-8">
      <div className="max-w-6xl mx-auto px-4 flex gap-8">
        {/* Slide Preview Panel */}
        <div className="w-1/4">
          <div className="flex items-center mb-4">
            <button
              className="btn-primary flex items-center"
              onClick={() => handleAddSlide(template)}
            >
              <Plus className="h-4 w-4 mr-1" /> Slide
            </button>
            <select
              className="ml-2 input-field"
              value={template}
              onChange={e => setTemplate(e.target.value)}
            >
              {SLIDE_TEMPLATES.map(t => (
                <option key={t.type} value={t.type}>{t.label}</option>
              ))}
            </select>
          </div>
          <div className="space-y-2">
            {slides.map((slide, idx) => (
              <div
                key={idx}
                className={`border rounded p-2 cursor-pointer ${selectedSlide === idx ? 'border-primary-600 bg-primary-50' : 'border-gray-200 bg-white'}`}
                onClick={() => setSelectedSlide(idx)}
              >
                <div className="text-xs font-semibold text-gray-700">{slide.type.charAt(0).toUpperCase() + slide.type.slice(1)} Slide</div>
                <div className="truncate text-gray-500 text-xs">{slide.title || slide.question || 'Untitled'}</div>
              </div>
            ))}
          </div>
        </div>

        {/* Slide Editor */}
        <div className="flex-1 bg-white rounded-lg shadow-lg p-8">
          <div className="mb-4 flex items-center justify-between">
            <div className="flex items-center gap-2">
              <Link to={`/course/${courseId}`} className="btn-secondary">
                <ArrowLeft className="h-4 w-4 mr-2" /> Back to Course
              </Link>
              <span className="text-lg font-semibold text-gray-700">Slide Editor</span>
            </div>
            <button className="btn-primary">Present</button>
          </div>

          {/* Slide Templates Toolbar */}
          <div className="mb-4 flex gap-2">
            {SLIDE_TEMPLATES.map(t => (
              <button
                key={t.type}
                className={`btn-secondary ${template === t.type ? 'bg-primary-100 text-primary-700' : ''}`}
                onClick={() => setTemplate(t.type)}
              >
                {t.label}
              </button>
            ))}
            <button className="btn-secondary" onClick={() => setSlides(prev => prev.map((s, i) => i === selectedSlide ? { ...s, ...SLIDE_TEMPLATES.find(t => t.type === template).content, type: template } : s))}>Clear</button>
          </div>

          {/* Slide Content Editor */}
          <div className="border rounded-lg p-8 min-h-[300px] bg-gray-50">
            {slides[selectedSlide]?.type === 'title' && (
              <>
                <input
                  className="w-full text-2xl font-bold mb-2 border-b border-gray-300 bg-transparent focus:outline-none"
                  placeholder="Click to add title..."
                  value={slides[selectedSlide].title}
                  onChange={e => handleSlideChange('title', e.target.value)}
                />
                <input
                  className="w-full text-lg text-gray-500 border-b border-gray-200 bg-transparent focus:outline-none"
                  placeholder="Click to add subtitle..."
                  value={slides[selectedSlide].subtitle}
                  onChange={e => handleSlideChange('subtitle', e.target.value)}
                />
              </>
            )}
            {slides[selectedSlide]?.type === 'basic' && (
              <>
                <input
                  className="w-full text-2xl font-bold mb-2 border-b border-gray-300 bg-transparent focus:outline-none"
                  placeholder="Click to add title..."
                  value={slides[selectedSlide].title}
                  onChange={e => handleSlideChange('title', e.target.value)}
                />
                <textarea
                  className="w-full text-base border-b border-gray-200 bg-transparent focus:outline-none"
                  placeholder="Click to add body..."
                  value={slides[selectedSlide].body}
                  onChange={e => handleSlideChange('body', e.target.value)}
                />
              </>
            )}
            {slides[selectedSlide]?.type === 'mcq' && (
              <>
                <input
                  className="w-full text-xl font-bold mb-2 border-b border-gray-300 bg-transparent focus:outline-none"
                  placeholder="Click to add question..."
                  value={slides[selectedSlide].question}
                  onChange={e => handleSlideChange('question', e.target.value)}
                />
                <div className="space-y-2 mt-2">
                  {slides[selectedSlide].options.map((opt, i) => (
                    <input
                      key={i}
                      className="w-full border-b border-gray-200 bg-transparent focus:outline-none"
                      placeholder={`Option ${i + 1}`}
                      value={opt}
                      onChange={e => {
                        setSlides(prev => prev.map((slide, idx) => idx === selectedSlide ? { ...slide, options: slide.options.map((o, oi) => oi === i ? e.target.value : o) } : slide));
                      }}
                    />
                  ))}
                </div>
              </>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default PresentationEditor;
